css:
.textcenter{
        text-align:center;
}
.borderclass{
    border-bottom: 1px solid dodgerblue;
}
table {
    width:100%;
    text-align:left;
    border-collapse: collapse;
}

table, td, th {
    /*width:50%;*/
    border: 1px solid dodgerblue;
    font-size:10px;

}
.print-format{
    padding: 12px !important;
}

html:
{% set address %}{% endset %}
{% if doc.company_address %}
    {%- set address=frappe.get_doc("Address",doc.company_address) -%}
{% endif %}

{% set so %}{% endset %}
{%for i in doc.items%}
{% if i.sales_order %}
    {%- set so=frappe.get_doc("Sales Order",i.sales_order) -%}
{% endif %}
{%endfor%}

{% set b_address %}{% endset %}
{% if doc.customer_address %}
    {%- set b_address=frappe.get_doc("Address",doc.customer_address) -%}
{% endif %}

{% set s_address %}{% endset %}
{% if doc.shipping_address_name %}
    {% set s_address=frappe.get_doc("Address",doc.shipping_address_name) %}
{% endif %}

{% set bd %}{% endset %}
{% if doc.company %}
    {% set bd=frappe.get_doc("Bank Account",{"company":doc.company,"is_default":1}) %}
{% endif %}

{% set disc_col=[] %}
{% for disc_per in doc.items %}
    {% if disc_per.discount_percentage %}
        {% set disc_col = disc_col.append(disc_per.discount_percentage) %}
    {% endif %}
{% endfor %}

{% if doc.additional_discount_percentage %}
    {% set rowspan = doc.taxes|length + 3 %}
{% else %}
    {% set rowspan = doc.taxes|length + 2 %}
{% endif %}
{% set company_upi %}{% endset %}
{% if doc.company %}
    {% set company_upi = frappe.get_doc("Company",doc.company) %}
{% endif %}

<!DOCTYPE HTML>
<div class="row">
    <div class="col-xs-9" style="text-align: left;font-size: 10px;">
        Invoice issued by under section 31 Rule 7 of CGST Act 2017 read with rule 1 of tax invoice.  
    </div>
    <div class="col-xs-3" style="text-align: right;">
        <b>{{ _("Original for Recipient") }}</b>
    </div>
</div>
<table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
        {% if doc.qrcode_image %}
            <td style="width: 18%;">
                <div><img src="{{doc.qrcode_image}}"></div>
            </td>
        {% endif %}
    </tr>
</table>
<table>
    <tr>
        <td style="text-align: center; font-size: 12px;">
            <b>TAX INVOICE</b>
        </td>
    </tr>
</table>
<div style="width:100%;font-size: 15px;">
    <table>
        <tr>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("Tax Invoice") }}:</b>&nbsp; <span style="font-size: 10px"><b>
                        {% if doc.name %}
                            {{ doc.name }}
                        {% endif %}</b></span>
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("Invoice Date") }}:</b>&nbsp; 
                        {% if doc.posting_date %}
                            {{ doc.get_formatted("posting_date") }}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("Sales Order No.") }}:</b>&nbsp;
                        {%for i in doc.items%}
                        {%if i.idx==1%}
                        {% if i.sales_order %}
                            {{i.sales_order}}
                        {% endif %}
                        {% endif %}
                        {%endfor%}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("SO Date") }}:</b>&nbsp;
                        {%for i in doc.items%}
                        {%if i.idx==1%}
                        {% if i.sales_order %}
                            {%- set so=frappe.get_doc("Sales Order",i.sales_order) -%}
                            {% if so.transaction_date %}
                                {{so.get_formatted("transaction_date")}}
                            {% endif %}
                        {% endif %}
                        {% endif %}
                        {%endfor%}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("PO No.") }}:</b>&nbsp;
                        {% if doc.po_no %} 
                            {{ doc.po_no }} 
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PO Date") }}:</b>&nbsp;
                        {% if doc.po_date %}
                            {{ doc.get_formatted("po_date") }}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("State") }}:</b>&nbsp;
                        {% if address.gst_state %}
                            {{address.gst_state}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("State Code") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin[0:2]}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("GSTIN") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin}}
                        {% endif %}
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("PAN") }}:</b>&nbsp;
                        {% if doc.company_gstin %}
                            {{doc.company_gstin[2:-3]}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-7">
                        <b>{{ _("Invoice Type") }}:</b>&nbsp; Regular
                    </div>
                    <div class="col-xs-5">
                        <b>{{ _("Reverse Charge ") }}:</b>&nbsp; 
                        {% if doc.reverse_charge %}
                            {{ doc.reverse_charge }}
                        {% endif %}
                    </div>
                </div>
            </td>
            <td style="width: 50%;">
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Transporter Name") }}: </b>&nbsp;
                        {% if doc.transporter_name %} 
                            {{ doc.transporter_name }} 
                        {% endif %}
                    </div>

                </div>
                <div class="row">
                    <div class="col-xs-12">
                        <b>{{ _("Destination") }}:</b>&nbsp;
                        {%for i in doc.items%}
                         {%if i.idx==1%}
                        {% if i.sales_order %}
                            {%- set so=frappe.get_doc("Sales Order",i.sales_order) -%}
                           {% if so.warehouse_address %}{{so.warehouse_address}}{% endif %}
                        {% endif %}
                        {% endif %}
                        {%endfor%}

                    </div>
                </div> 
                <div class="row">
                    <div class="col-xs-6">
                        <b>{{ _("Mode Of Trans.") }}: </b>&nbsp;
                        {% if doc.mode_of_transport %}
                            {{ doc.mode_of_transport }}
                        {% endif %}
                    </div>
                    <div class="col-xs-6">
                        <b>{{ _("VEH No")}}:</b>&nbsp;
                        {% if doc.vehicle_no %}
                            {{doc.vehicle_no}}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-6">
                        <b>{{ _("LR No.") }}: </b>&nbsp;
                        {% if doc.lr_no %} 
                            {{ doc.lr_no }} 
                        {% endif %}
                    </div>
                    <div class="col-xs-6">
                        <b>{{ _("LR Date") }}: </b>&nbsp;
                        {% if doc.lr_date %}
                            {{ doc.get_formatted("lr_date") }}
                        {% endif %}
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-6">
                        <b>{{ _("Freight") }}: </b>&nbsp;
                         {%for i in doc.items%}
                          {%if i.idx==1%}
                        {% if i.sales_order %}
                            {%- set so=frappe.get_doc("Sales Order",i.sales_order) -%}
                           {% if so.freight %}
                            {{ so.freight }}
                            {% endif %}
                        {% endif %}
                        {% endif %}
                        {%endfor%}

                    </div>
                    <div class="col-xs-6">
                        <b>{{ _("Due Date") }}:</b>&nbsp; 
                        {% if doc.posting_date %}
                            {{ doc.get_formatted("posting_date") }}
                        {% endif %}    
                    </div>
                </div>
                <div class="row">
                    <div class="col-xs-12">
                            <b>{{ _("Place Of Supply") }}:</b>&nbsp;
                            {% if s_address.city %} {{s_address.city}} {% endif %}
                            {% if s_address.state %} &nbsp;-&nbsp;{{s_address.state}} {% endif %}
                    </div>
                </div>
            </td>
        </tr>
        <tr>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Receiver(Bill to)")}}</b>
            </td>
            <td class="text-center" style="width: 50%;">
                <b>{{ _("Details of Consignee(Shipped to)")}}</b>
            </td>
        </tr>
        <tr>
            <td style="width: 50%;">

                <b>{{ _("Customer Name") }}:&nbsp;
                {% if b_address.address_title %}
                    {{b_address.address_title}}
                {% endif %} </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                {% if b_address.address_line1 %} 
                    {{b_address.address_line1}}, 
                {% endif %}<br>
                {% if b_address.address_line2 %} 
                    {{b_address.address_line2}} ,
                {% endif %}<br>
                {% if b_address.city %} 
                    {{b_address.city}} 
                {% endif %}
                {% if b_address.pincode %} 
                    -{{b_address.pincode}}, 
                {% endif %}
                {% if b_address.gst_state %} 
                    {{b_address.gst_state}}, 
                {% endif %}
                {% if b_address.country %} 
                    {{b_address.country}}.
                {% endif %}<br>
                <b>{{ _("Mobile No.") }}:</b>&nbsp;
                {% if b_address.phone %}
                    {{b_address.phone}}
                {% endif %}<br>
                <b>{{ _("Email") }}:</b>&nbsp;
                {% if b_address.email_id %}
                    {{b_address.email_id}}
                {% endif %}<br>
                <b>{{ _("Customer GSTIN") }}:</b>&nbsp;
                {% if b_address.gstin %}
                    {{b_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                {% if b_address.gst_state_number %}
                    {{b_address.gst_state_number}} 
                {% endif %}

            </td>
            <td style="width: 50%;">

                <b>{{ _("Customer Name") }}:&nbsp;
                    {% if s_address.address_line1 %}
                        {{s_address.address_title}}
                    {% endif %}
                </b><br>
                <b>{{ _("Address") }}:</b>&nbsp; 
                    {% if s_address.address_line1 %} 
                        {{s_address.address_line1}}, 
                    {% endif %}
                <br>
                {% if s_address.address_line2 %} 
                    {{s_address.address_line2}}, 
                {% endif %}<br>
                {% if s_address.city %} 
                    {{s_address.city}} 
                {% endif %}
                {% if s_address.pincode %} 
                    -{{s_address.pincode}}, 
                {% endif %}
                {% if s_address.gst_state %} 
                    {{s_address.gst_state}}, 
                {% endif %}
                {% if s_address.country %} 
                    {{s_address.country}}. 
                {% endif %}<br>
                <b>{{ _("Mobile No.") }}:</b>&nbsp;
                {% if s_address.phone %}
                    {{s_address.phone}}
                {% endif %}<br>
                <b>{{ _("Email") }}:</b>&nbsp;
                {% if s_address.email_id %}
                    {{s_address.email_id}}
                {% endif %}<br>
                <b>{{ _("Customer GSTIN") }}:</b>&nbsp;
                {% if s_address.gstin %}
                    {{s_address.gstin}}
                {% endif %}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <b>{{ _("State Code") }}:</b>&nbsp;
                    {% if s_address.gst_state_number %}
                        {{s_address.gst_state_number}}
                    {% endif %}
            </td>
        </tr>
    </table>
    <table style="margin-top: 2px;">
        <tr>
            <th><b>{{ _("Sr No.") }}</b></th>
            <th><b>{{ _("Item Name") }}</b></th>
            <th><b>{{ _("HSN") }}</b></th>
            <th><b>{{ _("MFG Date") }}</b></th>
            <th><b>{{ _("EXP Date") }}</b></th>
            <th><b>{{ _("Qty Packing") }}</b></th>
            <th><b>{{ _("Rate") }}</b></th>

            <th><b>{{ _("Amount") }}</b></th>
        </tr>
        {%- for item in doc.items -%}
        {% set batch %}{% endset %}
        {% if item.batch_no %}
            {% set batch = frappe.get_doc("Batch",item.batch_no) %}
        {% endif %}
        <tr>
            <td style="text-align: center;">{{ loop.index }}</td>
            <td>{{ item.item_name }}
                {% if item.batch_no %}
                    <br><span style="font-size: 9px"><b>Batch:</b>&nbsp;
                    {{ item.batch_no }}</span>
                {% endif %}
                {% if item.customer_item_code %}
                    <br><span style="font-size: 9px"><b>Cust. Item Name</b>&nbsp;
                    {{ item.customer_item_code }}</span>
                {% endif %}
            </td>
            <td>
                {% if item.gst_hsn_code %}
                    {{ item.gst_hsn_code }}
                {% endif %}
            </td>
            <td style="text-align: center;"> 
                {% if batch.manufacturing_date %}
                    {{ batch.get_formatted("manufacturing_date") }}
                {% endif %}
            </td>
            <td style="text-align: center;">
                 {% if batch.expiry_date %}
                    {{ batch.get_formatted("expiry_date") }}
                {% endif %}
            </td>
            <td style="text-align: center;">

                {%if item.packing_weight%}
                {% if item.total_weight %}
                    {{item.total_weight}}
                {% endif %}&nbsp;
                {% if item.weight_uom %}
                    {{item.weight_uom}}
                {% endif %}<br>
                  {{(item.qty/item.packing_weight) | int}} Nos X {{item.packing_weight | float}} KG PKG
                {% endif %}

                {%if not item.packing_weight%}

                {% if item.total_weight %}
                    {{item.total_weight}}
                {% endif %}&nbsp;
                {% if item.weight_uom %}
                    {{item.weight_uom}}
                {% endif %}<br>
                {% if item.qty %}
                    {{item.qty}}
                {% endif %}&nbsp;
                {% if item.uom %}
                    {{item.uom}}
                {% endif %}&nbsp;*&nbsp;
                {% if item.conversion_factor %}
                    {{item.conversion_factor}}
                {% endif %}
                {% endif %}
            </td>
            <td style="text-align: right;">
            {% if item.stock_uom_rate %}
                {{ item.get_formatted("stock_uom_rate") }}
            {% endif %}</td>

            <td style="text-align: right;width:12%;">
            {% if item.amount %}
                {{ item.get_formatted("amount") }}
            {% endif %}</td>
        </tr>
        {%- endfor -%}
        <tr>
            <th></th>
            <th></th>
            <th></th>
            <th></th>
            <th style="text-align: center;"><b>{{ _("Total") }}</b></th>
            <th style="text-align: center;"><b>{{doc.get_formatted("total_net_weight")}}&nbsp;KG</b></th>
            <th></th>

            <th style="text-align: right;"><b>{{ doc.get_formatted("total") }}</b></th>
        </tr>
    </table>


    <table style="margin-top: 2px;">
        <tr>
            <td rowspan ="{{ rowspan }}" style ="width:57%;">
                <b>Total Amount in Words:</b><br>
                {% if doc.in_words %}
                    {{ doc.in_words }}
                {% endif %}
                <hr style="margin-bottom: 2px;margin-top: 2px;">
                <b>Payment Terms:</b>&nbsp;
                {% if doc.payment_terms_template %}
                    {{doc.payment_terms_template}}
                {% endif %}<br>
                <b>{{ _("E-Waybill Number") }}:</b>&nbsp;
                {% if doc.ewaybill %}
                    {{doc.ewaybill}}
                {% endif %} &nbsp;&nbsp;
                <b>{{ _("E-Waybill Date ") }}:</b>&nbsp; 
                {% if doc.e_way_bill_date %} 
                    {{doc.get_formatted("e_way_bill_date")}}
                {% endif %}
            </td>
            <td style ="width:28%;" class ="text-right">
                <b>Total Amount:</b>
            </td>
            <td style ="width:11%;" class ="text-right">{{ doc.get_formatted("total") }}</td>
        </tr>
        {% if doc.additional_discount_percentage %}
            <tr>
                <td style ="width:28%;" class ="text-right"><b>Additional Disc. on {{doc.apply_discount_on}}: &nbsp;{{doc.additional_discount_percentage}}%</b></td>
                <td style ="width:11%;" class ="text-right"> -&nbsp;{{ doc.get_formatted("discount_amount")}}</td>
            </tr>
        {% endif %}
        <tr>
            <td style ="width:28%;" class ="text-right"><b>Taxable Value:</b></td>
            <td style ="width:11%;" class ="text-right"> &nbsp;
            {{ doc.get_formatted("net_total")}}</td>
        </tr>
        {%- for tax in doc.taxes -%}
        <tr>
            <td style ="width:28%;" class ="text-right">
                <b>{{ tax.description }}</b>
            </td>
            <td style ="width:11%;" class ="text-right">
                {{ tax.get_formatted("tax_amount") }}
            </td>
        </tr>
        {%- endfor -%}
        <tr>
            <td style ="width:57%;">

                <b>Account Name :</b>&nbsp;
                {% if bd.account_name %}
                    {{bd.account_name}}
                {% endif %}<br>
            </td>
            <td style ="width:28%;" class ="text-right"><b>Total:</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("grand_total")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>Bank Name :</b>&nbsp;
                {% if bd.bank %}
                    {{bd.bank}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Account Number :</b>&nbsp;
                {% if bd.bank_account_no %}
                    {{bd.bank_account_no}}
                {% endif %}</td>
            <td style ="width:28%;" class ="text-right"><b>Round Off Amount :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounding_adjustment")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>IFSC Code :</b>&nbsp;
                {% if bd.ifsc_code %}
                    {{bd.ifsc_code}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Branch Code :</b>&nbsp;
                {% if bd.branch_code %}
                    {{bd.branch_code}}
                {% endif %}<br></td>
            <td style ="width:28%;" class ="text-right"><b>Invoice Value :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounded_total")}}</td>
        </tr>
    </table>
    {% if doc.other_charges_calculation %}
    <div style="margin-top: -15px; margin-bottom: -15px;">{{ doc.other_charges_calculation }} </div>
    {% endif %}
    <table style="margin-top: 0px;">
        <tr>
            <td style ="width:50%;" rowspan =""><b>Terms and Conditions :</b><br>
                {% if doc.terms %}
                    <span style="font-size: 7px;">{{ doc.terms }}</span>
                {% endif %}                
            </td>
            {% if company_upi.upi_qr_code_image %}
                <td style="width: 20%">
                    <div><img src="{% if company_upi.upi_qr_code_image %}{{company_upi.upi_qr_code_image}}{% endif %}" width="130" height="130"></div>
                    <div class="text-center"><b>UPI ID:{% if company_upi.upi_id %}{{ company_upi.upi_id }}{% endif %}</b></div>
                </td>
            {% endif %}
            <td style="font-size:10px; width: 30%"><br>
                <p class="text-center">For <b>
                {% if doc.company %}
                    {{doc.company}}
                {% endif %}
                <!--</b><br></p><br><br><br><br><br><br><br><br>-->
                {% set img=get_img_url(doc) %}
                <img src={{img}} alt="signature" width="100" height="100">

                <p class ="text-center"><b>Authorised Signatory</b></p>
            </td>
        </tr>
    </table>
</div>


<div class="page-break"></div>

