            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("grand_total")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>Bank Name :</b>&nbsp;
                {% if bd.bank %}
                    {{bd.bank}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Account Number :</b>&nbsp;
                {% if bd.bank_account_no %}
                    {{bd.bank_account_no}}
                {% endif %}</td>
            <td style ="width:28%;" class ="text-right"><b>Round Off Amount :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounding_adjustment")}}</td>
        </tr>
        <tr>

            <td style ="width:57%;"><b>IFSC Code :</b>&nbsp;
                {% if bd.ifsc_code %}
                    {{bd.ifsc_code}}
                {% endif %}
                &nbsp;&nbsp;&nbsp;<b>Branch Code :</b>&nbsp;
                {% if bd.branch_code %}
                    {{bd.branch_code}}
                {% endif %}<br></td>
            <td style ="width:28%;" class ="text-right"><b>Invoice Value :</b></td>
            <td style ="width:11%;" class ="text-right"> {{ doc.get_formatted("rounded_total")}}</td>
        </tr>
    </table>
    {% if doc.other_charges_calculation %}
    <div style="margin-top: -15px; margin-bottom: -15px;">{{ doc.other_charges_calculation }} </div>
    {% endif %}
    <table style="margin-top: 0px;">
        <tr>
            <td style ="width:50%;" rowspan =""><b>Terms and Conditions :</b><br>
                {% if doc.terms %}
                    <span style="font-size: 7px;">{{ doc.terms }}</span>
                {% endif %}                
            </td>
            {% if company_upi.upi_qr_code_image %}
                <td style="width: 20%">
                    <div><img src="{% if company_upi.upi_qr_code_image %}{{company_upi.upi_qr_code_image}}{% endif %}" width="130" height="130"></div>
                    <div class="text-center"><b>UPI ID:{% if company_upi.upi_id %}{{ company_upi.upi_id }}{% endif %}</b></div>
                </td>
            {% endif %}
            <td style="font-size:10px; width: 30%"><br>
                <p class="text-center">For <b>
                {% if doc.company %}
                    {{doc.company}}
                {% endif %}
                <!--</b><br></p><br><br><br><br><br><br><br><br>-->
                {% set img=get_img_url(doc) %}
                <img src={{img}} alt="signature" width="100" height="100">
                <p class ="text-center"><b>Authorised Signatory</b></p>
            </td>
        </tr>
    </table>
</div>
